// src/components/Booking.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Booking.css';

function Booking() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    username: '',
    service: '',
    date: '',
    time: '',
    paymentMethod: ''
  });

  const [errors, setErrors] = useState({
    username: '',
    service: '',
    date: '',
    time: '',
    paymentMethod: ''
  });

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };

  const validateDate = (date) => {
    const selectedDate = new Date(date);
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    
    // Check if date is in the future
    return selectedDate >= today;
  };

  const validateTime = (time) => {
    // Basic business hours validation (8 AM to 6 PM)
    const [hours] = time.split(':');
    const hourNum = parseInt(hours, 10);
    return hourNum >= 8 && hourNum < 18;
  };

  const validateForm = () => {
    let isValid = true;
    const newErrors = {
      username: '',
      service: '',
      date: '',
      time: '',
      paymentMethod: ''
    };

    // Username (email) validation
    if (!formData.username.trim()) {
      newErrors.username = 'Email is required';
      isValid = false;
    } else if (!validateEmail(formData.username)) {
      newErrors.username = 'Please enter a valid email address';
      isValid = false;
    }

    // Service validation
    if (!formData.service) {
      newErrors.service = 'Please select a service';
      isValid = false;
    }

    // Date validation
    if (!formData.date) {
      newErrors.date = 'Please select a date';
      isValid = false;
    } else if (!validateDate(formData.date)) {
      newErrors.date = 'Please select a future date';
      isValid = false;
    }

    // Time validation
    if (!formData.time) {
      newErrors.time = 'Please select a time';
      isValid = false;
    } else if (!validateTime(formData.time)) {
      newErrors.time = 'Please select a time between 8 AM and 6 PM';
      isValid = false;
    }

    // Payment method validation
    if (!formData.paymentMethod) {
      newErrors.paymentMethod = 'Please select a payment method';
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Booking failed');
      }

      const data = await response.json();
      navigateToPayment();
    } catch (error) {
      setErrors(prev => ({
        ...prev,
        submit: error.message
      }));
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
    
    // Clear error when user starts typing
    setErrors(prev => ({
      ...prev,
      [name]: '',
      submit: ''
    }));
  };

  return (
    <div className="booking">
      <h1>Book an Appointment</h1>
      <form onSubmit={handleSubmit}>
        {errors.submit && (
          <div className="error-message" style={{ color: 'red', marginBottom: '1rem' }}>
            {errors.submit}
          </div>
        )}
        
        <label className='label'>
          Username:
          <input
            type="email"
            name="username"
            placeholder='johndoe@gmail.com'
            value={formData.username}
            onChange={handleInputChange}
            required
          />
          {errors.username && (
            <span className="error-message">{errors.username}</span>
          )}
        </label>

        <label>
          Service:
          <select
            name="service"
            value={formData.service}
            onChange={handleInputChange}
            required
          >
            <option value="">Select a service</option>
            <option value="haircut">Haircut $25</option>
            <option value="shave">Shave $15</option>
            <option value="beard_trim">Beard Trim $10</option>
            <option value="beard_trim & haircut">Beard Trim & Haircut $35</option>
          </select>
          {errors.service && (
            <span className="error-message">{errors.service}</span>
          )}
        </label>

        <label>
          Date:
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            min={new Date().toISOString().split('T')[0]}
            required
          />
          {errors.date && (
            <span className="error-message">{errors.date}</span>
          )}
        </label>

        <label>
          Time:
          <input
            type="time"
            name="time"
            value={formData.time}
            onChange={handleInputChange}
            min="08:00"
            max="18:00"
            required
          />
          {errors.time && (
            <span className="error-message">{errors.time}</span>
          )}
        </label>

        <label>
          Payment Method:
          <select
            name="paymentMethod"
            value={formData.paymentMethod}
            onChange={handleInputChange}
            required
          >
            <option value="">Select a payment method</option>
            <option value="credit_card">Credit Card</option>
            <option value="debit_card">Debit Card</option>
            <option value="cash">Cash</option>
            <option value="cash_app">CashApp</option>
            <option value="paypal">PayPal</option>
            <option value="venmo">Venmo</option>
            <option value="zelle">Zelle</option>
          </select>
          {errors.paymentMethod && (
            <span className="error-message">{errors.paymentMethod}</span>
          )}
        </label>

        <button type="submit">Book Now</button>
      </form>
    </div>
  );
}

export default Booking;
